import styles from "./AddCovidForm.module.css";
import React, { useState } from "react";
import { nanoid } from "nanoid";
import Alert from "../Alert/Alert";
import Button from "../Ui/Button";
import paint from "../Img/Imagecovid.png";

function AddCovidForm({ pasiens, setPasiens }) {
  const [formData, setFormData] = useState({
    provinsi: "",
    status: "",
    jumlah: "",
  });

  const { provinsi, status, jumlah } = formData;

  // buat  fungsi handleProvinsi
  function handleChange(e) {
    // Destructuring name dan value.
    const { name, value } = e.target;

    /**
     * Mengupdate state berupa object:
     * - Menggunakan spread operator:
     * - Update property berdasarkan apapun nilai name.
     */
    setFormData({
      ...formData,
      [name]: value,
    });
  }

  function validate() {
    if (provinsi === "") {
      setIsProvinsiError(true);
      return false;
    } else if (status === "") {
      setIsStatusError(true);
      setIsProvinsiError(false);
      return false;
    } else if (jumlah === "") {
      setIsJumlahError(true);
      setIsStatusError(false);
      return false;
    } else {
      setIsProvinsiError(false);
      setIsStatusError(false);
      return true;
    }
  }

  // state untuk error input form
  const [isProvinsiError, setIsProvinsiError] = useState(false);
  const [isStatusError, setIsStatusError] = useState(false);
  const [isJumlahError, setIsJumlahError] = useState(false);

  function addPasien() {
    const checkData = () => {
      if (status === "meninggal") {
        return {
          meninggal: jumlah,
          sembuh: 0,
          dirawat: 0,
          positif: 0,
        };
      }

      if (status === "dirawat") {
        return {
          dirawat: jumlah,
          meninggal: 0,
          sembuh: 0,
          positif: 0,
        };
      }

      if (status === "positif") {
        return {
          dirawat: 0,
          meninggal: 0,
          sembuh: 0,
          positif: jumlah,
        };
      }

      return {
        dirawat: 0,
        meninggal: 0,
        positif: 0,
        sembuh: jumlah,
      };
    };

    const pasien = {
      id: nanoid(),
      provinsi: provinsi,
      ...checkData(),
    };

    // SOLVED: HOW TO ADD MOVIE TO MOVIES :)
    setPasiens([...pasiens, pasien]);
  }

  function handleSubmit(e) {
    e.preventDefault();

    validate() && addPasien();
  }

  return (
    <div className={styles.container}>
      <section className={styles.form}>
        <div className={styles.form__left}>
          <img className={styles.form__image} src={FormImage} alt="" />
        </div>
        <div className={styles.form__right}>
          <h2 className={styles.form__title}>Form Covid</h2>
          <form onSubmit={handleSubmit}>
            {/* ------ Provinsi ------ */}
            <div className={styles.form__group}>
              <label className={styles.form__label}>Provinsi</label>
              <select
                className={styles.form__select}
                value={kota}
                onChange={handleKota}
                name="kota"
                id="kota"
              >
                <option value="Select Provinsi">Select Provinsi</option>
                {provinces.map((province, key) => {
                  return (
                    <option value={province.kota} key={key}>
                      {province.kota}
                    </option>
                  );
                })}
              </select>
            </div>
            {/* ----- End Provinsi -----*/}

            {/* ----- Status -----*/}
            <div className={styles.form__group}>
              <label className={styles.form__label}>Status</label>
              <select
                id="status"
                className={styles.form__select}
                name="status"
                value={status}
                onChange={handleStatus}
              >
                <option value="Select Status">Select Status</option>
                {listStatus.map((statusCovid, key) => {
                  return (
                    <option value={statusCovid} key={key}>
                      {statusCovid}
                    </option>
                  );
                })}
              </select>
            </div>
            {/* ----- End Status -----*/}

            {/* ----- Jumlah -----*/}
            <div className={styles.form__group}>
              <label htmlFor="jumlah" className={styles.form__label}>
                Jumlah
              </label>
              <input
                onChange={handleJumlah}
                id="jumlah"
                className={styles.form__input}
                name="jumlah"
                type="number"
                value={jumlah}
              />
              {isJumlahError && <Alert>Jumlah Wajib Diisi</Alert>}
            </div>
            {/* ----- End Jumlah -----*/}

            {/* ----- Button -----*/}
            <div>
              <Button full>Submit</Button>
            </div>
            {/* ----- End Button -----*/}
          </form>
        </div>
      </section>
    </div>
  );
}

export default AddCovidForm;
