// import { nanoid } from "nanoid";
 import { useState } from "react";
// import Alert from "../Alert";
import styles from "./AddCovidForm.module.css";
import FormImage from "./../../asset/img/Form image.png"

// Menangkap props
function AddCovidForm(props) {
 

  return (
    <div className={styles.container}>
      <section className={styles.form}>
        <div className={styles.form__left}>
          <img
            className={styles.form__image}
            src={FormImage}
            alt=""
          />
        </div>
        <div className={styles.form__right}>
          <h2 className={styles.form__title}>Form Covid</h2>
          <form onSubmit="">
            <div className={styles.form__group}>
              <label htmlFor="status" className={styles.form__label}>
                Provinsi
              </label>
              <input
                id="provinsi"
                className={styles.form__input}
                type="option"
                name="title"
                // Memberikan value input: title
                // value={provinsi}
                // Memberikan event onChange
                // onChange={handleProvinsi}
              />
              {/*
               * Menambahkan infline if: operator &&
               * Jika isTitleError true maka render error
               */}
              {/* {isProvinsiError && <Alert>Title harus ditambahkan</Alert>} */}
            </div>
            <div className={styles.form__group}>
              <label htmlFor="status" className={styles.form__label}>
                Status
              </label>
              <select
                id="status"
                className={styles.form__select}
                name="status"
                // value={status}
                // onChange={handleStatus}
                >
                <option value="Pilih">Pilih</option>
                <option value="Positif">Positif</option>
                <option value="Sembuh">Sembuh</option>
                <option value="Dirawat">Dirawat</option>
                <option value="Meninggal">Meninggal</option>
              </select>
              {/*
               * Menambahkan infline if: operator &&
               * Jika isDateError true maka render error
               */}
              {/* {isStatusError && <Alert>Date harus ditambahkan</Alert>} */}
            </div>
            <div className={styles.form__group}>
              <label htmlFor="jumlah" className={styles.form__label}>
                Jumlah
              </label>
              <input
                // onChange={handleJumlah}
                id="jumlah"
                className={styles.form__input}
                name="jumlah"
                type="integer"
                // value={jumlah}
              />
              {/* {isJumlahError && <Alert>Poster Harus ditambahkan</Alert>} */}
            </div>
            <div>
              <button className={styles.form__button}>Add Movie</button>
            </div>
          </form>
        </div>
      </section>
    </div>
  );
}

export default AddCovidForm;
