import Card from "../Card"
import styles from "./GlobalCard.module.css"
import data from "../../../utils/constants/indonesia"
import { useState } from "react"
import { nanoid } from "nanoid"


function GlobalCard() {
    const [globalcard, setGlobalCard] = useState(data);

    function handleclick() {
    const card = {
    id: nanoid(),
    status: "Positif",
    total: 6012818,
    detail: "https://covid19.mathdro.id/api/countries/indonesia/confirmed",
    };
    setGlobalCard([...globalcard,  card ]);
}

return (
    <div className={styles.container}>
        <section className={styles.globalcard}>
            <h2 className={styles.card__title}>Indonesia</h2>
            <h3 className={styles.card__subtitle}>Data Covid Berdasarkan Indonesia</h3>
            <div className={styles.card__container}>
                {
                    globalcard.data.map((card) => (
                        <Card key={card.status} card={card} />
                    )) 
                }
            </div>

        </section>

    </div>
)
}


export default GlobalCard;
